import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Link } from 'react-router-dom';

function HomePage() {

    return (
            <div className="App">
              <header className="App-header">

              </header>
              <body className="App-body">
                <h1>Scopify</h1>
                <h3>Generate your horoscope playlist based off your sign and listening habits.</h3>
                <Link to="/ChooseZodiac">
                    <button onClick={runScript}>Log in with Spotify</button>
                </Link>
              </body>
              <footer>
                <p>created by Emily Zacharias | &copy; 2024 | about | policy</p>
              </footer>
            </div>
    );
}


function runScript() {
    // Fetch the script file
    fetch('/path/to/script.js')
        .then(response => response.text())
        .then(scriptText => {
            // Create a script element
            const script = document.createElement('script');
            script.type = 'text/javascript';
            script.text = scriptText;

            // Append the script to the document's body
            document.body.appendChild(script);
        })
        .catch(error => {
            console.error('Error loading script:', error);
        });
} 

export default HomePage;
