import logo from './logo.svg';
import './App.css';

const token = 'BQC56JhfPKbvHjGBsmAtEKCc-2Dcm7CT61rAYnOHg11maBBmE1xiukqcxcgyXu6WXzIU7mf5kQioBH_3-UW7uKdtp4gn-TWamIx5swblEB3CaHiJ6mvzSghFDPh9ByOyG7HUnV2Tu1Gro2vpJl5p0zCU6oIoKxb3QfwrJ1xAiAYCTWDmtiDCZkcfD88AY1HwoUJEUdo9_9k92hM7-e4ainqaN2NKq_zLN1qC0A0iT9EKfv91bFJ8whwVL_oWYmM';
async function fetchWebApi(endpoint, method, body) {
  const res = await fetch(`https://api.spotify.com/${endpoint}`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
    method,
    body:JSON.stringify(body)
  });
  return await res.json();
}

async function getTopTracks(){
  // Endpoint reference : https://developer.spotify.com/documentation/web-api/reference/get-users-top-artists-and-tracks
  return (await fetchWebApi(
    'v1/me/top/tracks?time_range=long_term&limit=5', 'GET'
  )).items;
}

const topTracks = await getTopTracks();
console.log(
  topTracks?.map(
    ({name, artists}) =>
      `${name} by ${artists.map(artist => artist.name).join(', ')}`
  )
);

